package com.jolin.api;import com.github.xiaoymin.knife4j.annotations.ApiOperationSupport;import com.github.xiaoymin.knife4j.annotations.ApiSupport;import com.jolin.common.annotation.BaseJoinId;import com.jolin.common.api.BaseJoinController;import com.jolin.common.dto.ResultDTO;import com.jolin.dto.DeptInfoDTO;import com.jolin.dto.DeptUserJoinDTO;import com.jolin.dto.UserInfoDTO;import com.jolin.service.IDeptInfoService;import com.jolin.service.IDeptUserService;import com.jolin.service.IUserInfoService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.Arrays;import java.util.List;@RestController@Api(tags = {"10.用户与部门关联操作接口"})@ApiSupport(order = 10)@RequestMapping("/sys/dept-user")public class DeptUserController extends BaseJoinController<IDeptUserService, DeptUserJoinDTO> {    @Autowired    private IDeptInfoService iDeptInfoService;    @Autowired    private IUserInfoService iUserInfoService;    @ApiOperation(value = "9.根据用户Id查询用户所有部门")    @ApiOperationSupport(order = 9)    @GetMapping(value = "/findDeptInfosByUserId")    public ResultDTO findDeptInfosByUserId(String userId) {        List<String> deptIds = iBaseService.findDeptIdsByUserIds(Arrays.asList(userId));        List<DeptInfoDTO> deptInfoDTOS = iDeptInfoService.findByIds(deptIds);        return new ResultDTO(deptInfoDTOS);    }    @ApiOperation(value = "10.根据部门Id查询部门下所有用户")    @ApiOperationSupport(order = 10)    @GetMapping(value = "/findUsersByDeptInfoId")    public ResultDTO findUsersByDeptInfoId(String deptInfoId) {        List<String> userIds = iBaseService.findUserIdsByDeptIds(Arrays.asList(deptInfoId));        List<UserInfoDTO> userInfoDTOS = iUserInfoService.findByIds(userIds);        return new ResultDTO(userInfoDTOS);    }    @ApiOperation(value = "11.根据用户Id集合查询用户与部门关联关系")    @ApiOperationSupport(order = 11)    @PostMapping(value = "/findByUserIds")    public ResultDTO<List<DeptUserJoinDTO>> findByUserIds(@RequestBody List<String> userIds) {        List<String> ids = iBaseService.findIdsByJoinIds(userIds, BaseJoinId.Index.second);        List<DeptUserJoinDTO> dtoList = iBaseService.findByIds(ids);        return new ResultDTO(dtoList);    }}