package com.jolin.api;import com.github.xiaoymin.knife4j.annotations.ApiOperationSupport;import com.github.xiaoymin.knife4j.annotations.ApiSupport;import com.jolin.common.api.BaseJoinController;import com.jolin.common.dto.ResultDTO;import com.jolin.dto.UserGroupDTO;import com.jolin.dto.UserGroupUserJoinDTO;import com.jolin.dto.UserInfoDTO;import com.jolin.service.IUserGroupService;import com.jolin.service.IUserGroupUserService;import com.jolin.service.IUserInfoService;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Arrays;import java.util.List;@RestController@Api(tags = {"07.用户组与用户关联操作接口"})@ApiSupport(order = 7)@RequestMapping("/sys/group-user")public class UserGroupUserController extends BaseJoinController<IUserGroupUserService, UserGroupUserJoinDTO> {    @Autowired    private IUserGroupService iUserGroupService;    @Autowired    private IUserInfoService iUserInfoService;    @ApiOperation(value = "7.根据用户Id查询所有用户组")    @ApiOperationSupport(order = 7)    @GetMapping(value = "/findGroupsByUserId")    public ResultDTO findGroupsByUserId(String userId) {        List<String> groupIds = iBaseService.findGroupIdsByUserIds(Arrays.asList(userId));        List<UserGroupDTO> groupDTOS = iUserGroupService.findByIds(groupIds);        return new ResultDTO(groupDTOS);    }    @ApiOperation(value = "8.根据用户组Id查询所有用户")    @ApiOperationSupport(order = 8)    @GetMapping(value = "/findUsersByGroupId")    public ResultDTO findUsersByGroupId(String groupId) {        List<String> userIds = iBaseService.findUserIdsByGroupIds(Arrays.asList(groupId));        List<UserInfoDTO> userInfoDTOS = iUserInfoService.findByIds(userIds);        return new ResultDTO(userInfoDTOS);    }}